// Get all locations as array for dropdown
export const getAllLocationsForDropdown = () => {
  return Object.entries(allIndianLocations).map(([key, value]) => ({
    key,
    name: value.name,
    state: value.state,
    type: value.type
  })).sort((a, b) => a.name.localeCompare(b.name));
};

// Get locations by state
export const getLocationsByState = (state: string) => {
  return Object.entries(allIndianLocations)
    .filter(([_, value]) => value.state.toLowerCase() === state.toLowerCase())
    .map(([key, value]) => ({
      key,
      ...value
    }));
};

// Get all unique states
export const getAllStates = () => {
  const states = new Set(Object.values(allIndianLocations).map(loc => loc.state));
  return Array.from(states).sort();
};

// Search locations
export const searchLocations = (query: string) => {
  const lowerQuery = query.toLowerCase();
  return Object.entries(allIndianLocations)
    .filter(([key, value]) => 
      key.includes(lowerQuery) ||
      value.name.toLowerCase().includes(lowerQuery) ||
      value.state.toLowerCase().includes(lowerQuery)
    )
    .map(([key, value]) => ({
      key,
      ...value
    }));
};

