    "manali": { "delhi": 530, "shimla": 250, "leh": 480, "dharamshala": 230, "chandigarh": 310 },
    "udaipur": { "jaipur": 400, "jodhpur": 250, "ahmedabad": 260, "mount abu": 160, "chittorgarh": 110 },
    "varanasi": { "delhi": 800, "lucknow": 300, "kolkata": 680, "prayagraj": 125, "bodhgaya": 250 },
    "rishikesh": { "delhi": 240, "haridwar": 25, "dehradun": 45, "mussoorie": 80 },
